<?php/* Standard Header */require 'utilities/db.php';require 'utilities/model.php';require 'utilities/view.php';session_start();if (!isset($_SESSION["user"])){	// user sudah login, dialihkan ke halaman lain	header('Location: index.php');}else{	$view = new View('views/profile/profile.tpl');	$view->set("username",$_SESSION["user"]["username"]);	$view->set("fullname",$_SESSION["user"]["full_name"]);	$view->set("tgl_lahir",$_SESSION["user"]["tgl_lahir"]);	$view->set("pwd",$_SESSION["user"]["password"]);	$view->set("avat",$_SESSION["user"]["avatar"]);	$view->set('headTags','<script src="scripts/profile.js" type="application/javascript"></script>		<link rel="stylesheet" type="text/css" href="./styles/profile.css" />');	$view->set('title','Todolist | Profile');		echo $view->output();}