<?php/* Standard Header */require 'utilities/db.php';require 'utilities/model.php';require 'utilities/view.php';require 'models/user.php';session_start();if (!isset($_SESSION["user"])){	// user sudah login, dialihkan ke halaman lain	header('Location: index.php');} else if ($_SESSION["user"]["username"] == $_GET["username"]){	header('Location: profile.php');} else {	$user = new User();	$pengguna = $user->getUserDetail($_GET['username']);		if ($pengguna != NULL){		$view = new View('views/profileM/profileM.tpl');							$view->set("username",$pengguna['username']);			$view->set("fullname",$pengguna['full_name']);			$view->set("tgl_lahir",$pengguna['tgl_lahir']);			$view->set("email",$pengguna['email']);			$view->set("avat",$pengguna["avatar"]);					$view->set('headTags', '<script src="scripts/profileM.js" type="application/javascript"></script><link rel="stylesheet" type="text/css" href="./styles/profile.css" />');			$view->set('title','Todolist | Profile');	} else {	//die(json_encode($pengguna));		$view = new View('views/profileM/nouserm.tpl');	}		echo $view->output();	}